dataKeys.funcBody:
var value = prevValue + Math.random() * 100 - 50;
var multiplier = Math.pow(10, 2 || 0);
var value = Math.round(value * multiplier) / multiplier;
if (value < -1000) {
	value = -1000;
} else if (value > 1000) {
	value = 1000;
}
return value;

